    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Liste des formations !{% endblock %}

{% block stylesheets %}
<link rel=\"stylesheet\" href=\"/css/formation/formation.css\">
{% endblock %}

{% block body %}

<section class=\"head\">

    <h2>Liste des </h2>
    <h2><span class=\"vert\">Formations</span> Faites pour vous</h2>

</section>

<section class=\"options\">
    <div class=\"search-bar\">
        {{ form_start(form) }}
            {{ form_row(form.q) }}
            {{ form_row(form.complete) }}
        <button type=\"submit\">Filtrer</button>
        {{ form_end(form) }}
    </div>
    {% if app.user != null and app.user.roles == ['ROLE_USER'] %}
        <div class=\"filter\">
            <input type=\"checkbox\" id=\"complete\" name=\"Formations terminées\"></input>
            <label for=\"complete\">Masquer les formations terminées</label>
        </div>
    {% endif %}
</section>

<section class=\"wrapper\">

    {% for formation in formations %}

        <article>
            <img src=\"{{ formation.image }}\" alt=\"\">
            <h3 class=\"formation-title\">{{ formation.title }}</h3>
            <div class=\"content\">
                <p>{{ formation.content }}</p>
            </div>
            
            <a href=\"formation/{{ formation.id }}\" class=\"formation-btn\">Je me lance !</a>
        </article>

    {% endfor %}

</section>

{% endblock %}
", "formation/index.html.twig", "E:\\ecf\\ECF\\templates\\formation\\index.html.twig");
